streams.schema:
type contentFolders @createModel(accountRelation: LIST, description: "Dataverse:b8f44ab4-54ed-461f-94aa-43a409df236d | ContentFolder") {
  author: DID! @documentAccount
  version: CommitID! @documentVersion
  indexFolderId: String! @string(maxLength: 1000)
  mirrors: String! @string(maxLength: 300000000)
}

type pushchatgpgkey @createModel(accountRelation: LIST, description: "Dataverse:b8f44ab4-54ed-461f-94aa-43a409df236d | pushchatgpgkey") {
  owner: DID! @documentAccount
  pgp_key: String! @string(maxLength: 300000000)
  encrypted: String @string(maxLength: 300000000)
}

type xmtpkeycache @createModel(accountRelation: LIST, description: "Dataverse:b8f44ab4-54ed-461f-94aa-43a409df236d | xmtp key cache") {
  owner: DID! @documentAccount
  keys: String! @string(maxLength: 30000000)
  encrypted: String @string(maxLength: 300000000)
}
